import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import User from './components/User';
import Hobby from './components/Hobby';
import Restaurant from './components/Restaurants';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      restaurants: [],
      userInput: '',
      displayNumber: '5',
      currentPage: '0',
      query: '',
      filteredrestaurants: [],
      cRestaurants: null,
      elementCount: null,
      modifMod: false,
      insertMod: false,
      colMod: false,
      showMessage: false,
      message: '',
      notActiveClass: 'col-lg-12',
      activeClass: 'col-lg-8',
      selectedForModification: null,
    }
  }

  addHobby() {
    let oldHobbies = this.state.hobbies;
    this.setState({
      hobbies: oldHobbies.concat(this.input.value)
    });

    this.input.value = "";
  }

  removeHobby(hobby) {
		/*
		let oldHobbies = this.state.hobbies;
		let pos = this.state.hobbies.indexOf(hobby);
		oldHobbies.splice(pos, 1);
		*/
    const oldHobbies = this.state.hobbies.filter(
      (elem, index) => {
        return (elem !== hobby) ? elem : null;
      }
    );

    this.setState({
      hobbies: oldHobbies
    });
  }
  getDataFromServer(url) {
    console.log("--- GETTING DATA ---");
    fetch(url)
      .then(response => {
        response.json().then(res => {
          console.log(res);
          this.setState({ restaurants: res.data })
          this.setState({ filteredrestaurants: this.state.restaurants })
          // this.state.restaurants = res.data;
          //this.state.filteredrestaurants = this.state.restaurants;

          console.log(this.state.restaurants)

        })
      })
      .catch(err => {
        console.log("erreur dans le get : " + err)
      });
  }

  removeRestaurant(id, index) {
    let url = "http://localhost:8080/api/restaurants/" + id;
    console.log(id);
    console.log(url);

    fetch(url, {
        method: "DELETE",
    })
        .then((responseJSON) => {
          console.log("element supprimé")
            this.state.restaurants.splice(index, 1)
            this.state.filteredrestaurants.splice(index, 1)
            this.state.message = 'Ce restaurant a été supprimé'
            this.state.showMessage = true
            setTimeout(() => {
                this.state.showMessage = false
            }, 3000)
        })
        .catch(function (err) {
            console.log(err);
        });
}

  countRestaurants(gurl) {
    let url = gurl;
    fetch(url)
      .then(response => {
        response.json().then(res => {
          this.elementCount = res.data;
          this.cRestaurants = Math.ceil(this.elementCount / this.displayNumber);
        })
      })
      .catch(function (err) {
        console.log(err);
      });
  }

  componentWillMount() {
    console.log("Component will mount");
    // on va chercher des donnees sur le Web avec fetch, comme
    // on a fait avec VueJS
    this.countRestaurants('http://localhost:8080/api/restaurants/count')
    this.getDataFromServer('http://localhost:8080/api/restaurants?page=' + this.state.currentPage + '&pagesize=' + this.state.displayNumber);
  }

  showInsertForm() {
    this.colMod = true;
    this.modifMod = false;
    setTimeout(() => {
      this.insertMod = true;
    }, 300)
  }

  render() {
    console.log("render");
    // let list = 

    // let listAvecComponent = 
    // 		this.state.hobbies.map((el, index) => {
    // 		return <Hobby 
    // 						 name={el}
    // 						 key={index} 
    //              removeHobby={this.removeHobby.bind(this)} 
    // 						 />
    // 	}

    const options = [
      { value: 5, label: "5" },
      { value: 10, label: "10" },
      { value: 15, label: "15" }
    ]
    return (
      <body>
        <div id="app">
          <div className="container" style={{ position: "relative" }}>
            <br></br>
            <h2>Table des restaurants</h2>
            <button type="button" className="btn btn-dark mb-3" id="createButton" onClick={() => this.showInsertForm()}><i className="fa fa-plus"></i></button>
            <div style={{ position: "absolute", top: "15px", right: "15px", left: "15px", transition: "0.2s" }} id="succesAlert">
              <h6 className="alert-heading">Opération réussie</h6>
              <p>{this.state.message}</p>
            </div>
            <div className="row">
              <div id="tableArea" className={this.state.colMod ? this.state.activeClass : this.state.notActiveClass} style={{ transition: "0.2s" }}>
                <div className="input-group mb-3">
                  <div className="input-group-prepend">
                    <label className="input-group-text" for="elementPageDropDown">Elements par page</label>
                  </div>
                  <select model={this.state.displayNumber} class="custom-select" id="elementPageDropDown">
                    <option value="5">5</option>
                    <option value="10">10</option>
                    <option value="15">15</option>
                  </select>
                </div>
                <div className="input-group mb-3">
                  <input id="searchInput" className="form-control" placeholder="Chercher par nom" model={this.state.query}></input>
                </div>
                <table className="table table bordered" id="myTable">
                  <thead className="thead-dark">
                    <tr>
                      <th>Nom</th>
                      <th>Cuisine</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.restaurants.map(
                      (el, index) => {
                        return (
                          <tr for={(el, index) in this.state.filteredrestaurants}>
                            <td>{el.name}</td>
                            <td>{el.cuisine}</td>
                            <td>
                              <button className="btn btn-dark">Edit</button>
                              <button className="btn btn-dark" onClick={(e) => this.removeRestaurant(el._id, index)}>Delete</button>
                            </td>
                          </tr>
                        )
                      }
                    )
                    }
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
        {/* <button onClick={() => this.addHobby()}>Add Hobby</button>
        <p style={{color: (this.state.hobbies.length < 5) ? 'green' : 'red'}}>
            Nombre de Hobbies : {this.state.hobbies.length}
        </p>
        <ul>
        {listAvecComponent}
        </ul> */}

        <p>Un composant User ci-dessous:</p>
        <User name="Michel Buffa" />
        <User name="Gabriel Mopolo" />
      </body>

    );
  }
}

export default App;
